CREATE OR REPLACE PROCEDURE change_firm_name (old_name VARCHAR2, new_name VARCHAR2) IS
BEGIN
    UPDATE Firms
    SET firm_name = new_name
    WHERE firm_name = old_name;

    COMMIT;

    DBMS_OUTPUT.PUT_LINE('Firm name updated successfully.');
EXCEPTION
    WHEN OTHERS THEN
        DBMS_OUTPUT.PUT_LINE('Error updating firm name: ' || SQLERRM);
END;
/

