CREATE OR REPLACE TRIGGER km_stand_trigger
BEFORE INSERT OR UPDATE ON fahrzeug
FOR EACH ROW
BEGIN
    IF :NEW.km_stand > 100000 THEN
        DBMS_OUTPUT.PUT_LINE('Achtung: Der km-Stand des Fahrzeugs mit der Fahrgestellnummer ' || :NEW.fahrgestellnr || ' und der Marke ' || :NEW.marke || ' hat 100.000 km überschritten!');
    END IF;
END;
/

