% Defining the matrix A and the right-hand side vector B
A = [10 -1 2 0; -1 11 -1 3; 2 -1 10 -1; 0 3 -1 8];
B = [6; 25; -11; 15];

% Initial guess for the solution vector, maximum iterations, and tolerance
X = zeros(size(B));
max_iter = 100;
tol = 1e-6;
error = 1;
iter = 0;

% Gauss-Seidel iterations
while (iter < max_iter) && (error > tol)
    X_prev = X;
    for i = 1:length(A)
        temp_sum = 0;
        for j = 1:length(A)
            if i ~= j
                temp_sum = temp_sum + A(i, j) * X(j);
            end
        end
        X(i) = (B(i) - temp_sum) / A(i, i);
    end
    error = norm(X - X_prev, inf);
    iter = iter + 1;
end

