% Define simulation parameters
dt = 0.01; % Time interval
T = 30; % Total simulation time
time = 0:dt:T; % Time vector

% Define initial conditions
N_cars = 10; % Number of cars
v0 = 120 / 3.6; % Desired speed in m/s
xmin = 3; % Minimum gap to lead car in m
b = 4; % Comfortable deceleration in m/s^2
safed = 2; % Safe gap in m
a = 1.; % Maximum acceleration in m/s^2
L = [4.5, 5, 4.5, 5, 4.5, 5, 4.5, 5, 4.5, 5]; % Car length vector
W = [2, 2.2, 2, 2.2, 2, 2.2, 2, 2.2, 2, 2.2]; % Car width vector
x = cumsum([0, 10*ones(1,N_cars-1)]); % Car positions
v = v0*ones(1,N_cars); % Car speeds
dist = zeros(N_cars); % Car distances

% Run simulation
for i = 2:length(time)
    % Update positions
    x = x + v*dt;

    % Update speeds and distances
    for j = 1:N_cars
        % Calculate distance to lead car
        if j < N_cars
            xf = x(j+1) - L(j+1)/2;
            rl = xf - x(j) - L(j)/2;
        else
            rl = 100; % Assume no car ahead of last car
        end
        % Calculate acceleration
        delta_v = v(j) - v0;
        delta_d = xmin + v(j)*safed/(2*sqrt(a*b));
        delta_d_star = delta_d + max(0,delta_v*safed/(2*sqrt(a*b)));
        acc = a*(1 - (v(j)/v0)^4 - ((delta_d_star/rl)^2));
        % Limit deceleration
        acc = min(acc, -b);
        % Update speed and distance
        v(j) = max(0, v(j) + acc*dt);
        x(j) = x(j) + v(j)*dt;
        if j < N_cars
            dist(j) = rl;
        end
    end

    % Animate results
    clf;
    hold on;
    for j = 1:N_cars
        rectangle('Position',[x(j)-L(j)/2,(j-1)*W(j),L(j),W(j)],'FaceColor',[0.5,0.5,0.5]);
    end
    xlim([0, max(x)+50]);
    ylim([0, sum(W)]);
    xlabel('Distance (m)');
    ylabel('Lane width (m)');
    title(sprintf('Time = %0.1f s', time(i)));
    drawnow;
end

