        idx = (c-1) * size(imageLabelPad, 1) + r;
        which = imageLabelPad(idx);
        scratch(1) = idx;
        imageLabelPad(idx) = START;
        numPixels = 1;

        currentPixel = idx;
        initial_departure_direction = [];
        done = false;
        next_search_direction = 2;

        while ~done
            imageDirection = next_search_direction;
            found_next_pixel = false;

            for k = 1:length(imagePadOffsets)
                neighbor = currentPixel + imagePadOffsets(imageDirection);

                if imageLabelPad(neighbor) ~= 0
                    if (imageLabelPad(currentPixel) == START) && isempty(initial_departure_direction)
                        initial_departure_direction = imageDirection;
                    elseif (imageLabelPad(currentPixel) == START) && (initial_departure_direction == imageDirection)
                        done = true;
                        found_next_pixel = true;
                        break;
                    end

                    next_search_direction = nextSearchDirectionLookup(imageDirection);
                    found_next_pixel = true;

                    numPixels = numPixels + 1;
                    if numPixels > size(scratch, 1)
                        scratch(2 * size(scratch, 1)) = 0;
                    end

                    scratch(numPixels) = neighbor;
                    if imageLabelPad(neighbor) ~= START
                        imageLabelPad(neighbor) = BOUNDARY;
                    end

                    currentPixel = neighbor;
                    break;
                end

                imageDirection = searchDirectionLookup(imageDirection);
            end

            if ~found_next_pixel
                numPixels = 2;
                scratch(2) = scratch(1);
                done = true;
            end
        end

        [imageRow, imageColumn] = ind2sub(size(imageLabelPad), scratch(1:numPixels));
        B{which} = [imageRow-1, imageColumn-1];
    end
end

