% Prey-predator system exhibiting BT bifurcation with grazing and harvesting
% Parameters: alpha, beta, gamma, delta, h, k, r, s 
% x = prey population, y = predator population

alpha = 0.5;
beta = 0.5;
gamma = 0.5;
delta = 0.5;
h = 0.1;
k = 0.1;
r = 1.5;
s = 1.5;

% Define the ODE for the prey-predator system
f = @(t, y) [(r - alpha*y(2))*y(1) - h*y(1)^2/(k + y(1));
       (-s + beta*y(1))*y(2) - delta*y(1)*y(2)];

% Create a meshgrid for parameter space
[x,y] = meshgrid(0:0.1:2, 0:0.1:2);

% Calculate the eigenvalues of the Jacobian matrix
lambda1 = r - alpha*y - 2*h*x./(k+x);
lambda2 = -s*y + beta*x - delta*x;

% Define a threshold value for the minimum eigenvalue
eps = 0.001;

% Find the parameter values where the minimum eigenvalue is negative but close to zero
idx = find(min(lambda1, lambda2) < -eps & min(lambda1, lambda2) > -eps-0.001);

% Plot the bifurcation diagram
scatter(x(idx), y(idx), 'b.');
xlabel('r');
ylabel('s');
title('BT bifurcation diagram for the prey-predator system');

% Define the initial conditions for simulation
y0 = [0.8; 0.3];

% Define a range of values for r and s for simulation
rvec = 0:0.05:2;
svec = 0:0.05:2;

% Plot the time series for different values of r and s
figure;
for i = 1:length(rvec)
    for j = 1:length(svec)
        r = rvec(i);
        s = svec(j);
        [t, sol] = ode45(f, [0 50], y0);
        plot(sol(:,1), sol(:,2), 'k-', 'LineWidth', 0.5);
        hold on;
    end
end
