% Initial Parameters
area_size = 100;
num_nodes = 100;
BS = [50, 50];
p = 0.1;
E_init = 0.5;  % Initial energy of nodes
num_rounds = 4425;
alpha = 15;

% Generate random node positions
rng('shuffle');
nodes = area_size * rand(num_nodes, 2);

% Compute distances between nodes and the base station
dis_nodes_BS = pdist2(nodes, BS);

% Initialize energy values
E = E_init * ones(num_nodes, 1);

% Simulation
alive_nodes = num_nodes;
round = 0;
while round <= num_rounds
    round = round + 1;
    display(['Round: ', num2str(round)]);

    % Calculate threshold values for each node
    p1 = p * (1 - mod(round, 1 / p));
    T_n = (p1 / alpha) * (2 - sqrt(-dis_nodes_BS * (1 / (4 * alpha))));
    T_n = max(T_n, p / alpha);

    % Select CHs and SCHs
    CH_ind = [];
    SCH_ind = [];

    for i = 1:num_nodes
       if E(i) > 0
          CH_prob = rand;

          % Node i will be a CH or SCH if its probability is less than T_n(i)
          if CH_prob <= T_n(i)
             CH_ind = [CH_ind; i];
          else
             SCH_ind = [SCH_ind; i];
          end
       else
          alive_nodes = alive_nodes - 1;
       end
    end 
    
    % Data transmission (dummy calculation)
    for i = 1:length(CH_ind)
       E(CH_ind(i)) = E(CH_ind(i)) - 0.005;
    end
    
    for i = 1:length(SCH_ind)
       E(SCH_ind(i)) = E(SCH_ind(i)) - 0.0005;
    end
    
    % Update threshold values
    alive_nodes = sum(E > 0);
    
end

disp(['Alive nodes: ', num2str(alive_nodes)]);

