for i = 1:numIterations

  % Forward pass
  z2 = X*W1;
  a2 = sigmoid(z2);
  z3 = a2*W2;
  yHat = sigmoid(z3);

  % Backward pass
  delta3 = (yHat-y).*sigmoidGradient(z3);
  delta2 = delta3*W2' .* sigmoidGradient(z2);

  dW2 = a2'*delta3 + lambda*W2;
  dW1 = X'*delta2 + lambda*W1;

  % Update the weights
  W2 = W2 - alpha*dW2;
  W1 = W1 - alpha*dW1;
end

