CHIP Priority_Encoder {
    IN H, M, L;  // 输入端 H, M, L
    OUT[1:0] code;  // 输出端 code

    PARTS:
    // 使用AND门和OR门来实现优先级编码器
    // 优先级最高
    AND (H, M, L) -> H_AND_M_AND_L;
    // 优先级次高
    AND (H, M, L') -> H_AND_M_AND_L';
    // 优先级最低
    AND (H, M', L') -> H_AND_M'_AND_L';
    // 输出编码
    OR(H_AND_M_AND_L, H_AND_M_AND_L', H_AND_M'_AND_L') -> code[1:0];
}

CHIP Priority_Encoder {
    IN H, M, L;  // 输入端 H, M, L
    OUT[1:0] code;  // 输出端 code

    PARTS:
    // 使用MUX来实现优先级编码器
    // 比较优先级
    COMPARATOR (H, M, H_greater_M);
    COMPARATOR (H, L, H_greater_L);
    COMPARATOR (M, L, M_greater_L);
    // 设置code的值
    MUX (H_greater_M, H_greater_L, M) -> code[1];
    MUX (H_greater_L, M_greater_L, L) -> code[0];
}

CHIP COMPARATOR {
    IN A, B;  // 输入端 A, B
    OUT greater;  // 输出端 greater

    PARTS:
    OR(A, B) -> AB_OR;
    INV (A) -> A_INV;
    AND (A_INV, B) -> A_INV_AND_B;
    OR (AB_OR, A_INV_AND_B) -> greater;
}

