min_k = inf; % 初始化最小的k为无穷大
min_rho_s = 0; % 初始化最小k对应的rho_s
min_rho_w = 0; % 初始化最小k对应的rho_w
min_theta_s = 0; % 初始化最小k对应的theta_s
min_theta_w = 0; % 初始化最小k对应的theta_w
min_d = 0; % 初始化最小k对应的d
min_ka = 0; % 初始化最小k对应的ka
min_kf = 0; % 初始化最小k对应的kf

rho_s_values = 1:100; % rho_s的范围
rho_w_values = 1:100; % rho_w的范围
theta_s_values = 1:100; % theta_s的范围
theta_w_values = 1:100; % theta_w的范围
d_values = 1:100; % d的范围
ka_values = 1:100; % ka的范围
kf_values = 1:100; % kf的范围

for i = 1:length(rho_s_values)
    for j = 1:length(rho_w_values)
        for m = 1:length(theta_s_values)
            for n = 1:length(theta_w_values)
                for p = 1:length(d_values)
                    for q = 1:length(ka_values)
                        for r = 1:length(kf_values)
                            rho_s = rho_s_values(i);
                            rho_w = rho_w_values(j);
                            theta_s = theta_s_values(m);
                            theta_w = theta_w_values(n);
                            d = d_values(p);
                            ka = ka_values(q);
                            kf = kf_values(r);
                            [k, ~] = K(rho_s, rho_w, theta_s, theta_w, d, ka, kf);
                            if k < min_k
                                min_k = k;
                                min_rho_s = rho_s;
                                min_rho_w = rho_w;
                                min_theta_s = theta_s;
                                min_theta_w = theta_w;
                                min_d = d;
                                min_ka = ka;
                                min_kf = kf;
                            end
                        end
                    end
                end
            end
        end
    end
end

