numColors = size(colormap, 1);
colorImage = zeros(size(grayImage, 1), size(grayImage, 2), 3);

minIntensity = min(grayImage(:));
maxIntensity = max(grayImage(:));
intensityRange = maxIntensity - minIntensity;

for i = 1:size(grayImage, 1)
    for j = 1:size(grayImage, 2)
        intensity = grayImage(i, j);

        % Map intensity to the color range
        colorIndex = floor((intensity - minIntensity) / intensityRange * (numColors-1)) + 1;

        % Assign color based on colormap
        colorImage(i, j, :) = colormap(colorIndex, :);
    end
end

