% Constants
g = 9.8; % m/s^2
t_start = 0; % Start time (s)
t_end = 10; % End time (s)

% Time step values
dt_values = [0.1, 0.01, 0.001]; % Different time steps

% Function to calculate exact solution
exact_solution = @(t) -g * t;

% Loop over different time steps
for i = 1:length(dt_values)
    dt = dt_values(i);

    % Initialize arrays
    t = t_start:dt:t_end;
    v = zeros(size(t));

    % Euler method calculation
    v(1) = 0; % Initial velocity
    for j = 2:length(t)
        v(j) = v(j-1) + (-g) * dt;
    end

    % Calculate exact solution
    exact_v = exact_solution(t);

    % Plot numerical and exact solutions
    figure;
    plot(t, v, 'b-', 'LineWidth', 2);
    hold on;
    plot(t, exact_v, 'r--', 'LineWidth', 2);
    xlabel('Time (s)');
    ylabel('Velocity (m/s)');
    title(['Time Step = ' num2str(dt)]);
    legend('Numerical Solution', 'Exact Solution');
    grid on;
end

