% User-defined or default input values
M = 50;                 % Mass of pollution (gram)
Dx = 2;                 % Diffusion in x direction (cm^2/s)
Dy = 2;                 % Diffusion in y direction (cm^2/s)
W = 100;                % Width of channel (cm)
L = 350;                % Length of channel (cm)
R = 1;                  % Retardation coefficient
U = 1;                  % Flow rate in x direction (cm/s)
dt = 1;                 % Time step (s)
dx = 0.5;               % Moving step in horizental direction (cm)
dy = 0.5;               % Moving step in vertical direction (cm)
start_time = 1;         % Start time
end_time = 200;         % End time
source_x = 0;           % X-coordinate of pollution source
source_y = 50;          % Y-coordinate of pollution source

% Calculate grid size
n_x = ceil(W/dx);
n_y = ceil(L/dy);

% Initialize concentration matrix
C = zeros(n_y, n_x);

% Determine the grid coordinates of pollution source
source_x_index = ceil(source_x / dx);
source_y_index = ceil(source_y / dy);

% Loop through time steps
for t = start_time:dt:end_time
    % Calculate advection and diffusion terms
    X_adv = U * (C(:, [2:end, 1]) - C) / dx;        % Advection in x direction
    Y_adv = U * (C([2:end, 1], :) - C) / dy;        % Advection in y direction
    X_diff = Dx * (C(:, [2:end, 1]) - 2*C + C(:, [end, 1:end-1])) / dx^2;   % Diffusion in x direction
    Y_diff = Dy * (C([2:end, 1], :) - 2*C + C([end, 1:end-1], :)) / dy^2;   % Diffusion in y direction

    % Calculate new concentration
    C = C + dt * (X_adv + Y_adv + X_diff + Y_diff);

    % Add point source pollution
    C(source_y_index, source_x_index) = C(source_y_index, source_x_index) + M / (dx * dy);

    % Display current concentration distribution
    imagesc(C);
    colorbar;
    caxis([0, max(max(C))]);
    title(['Time: ', num2str(t)]);
    xlabel('X');
    ylabel('Y');
    drawnow;
end

