% 读取二值图像
binaryImage = imread('binary_image.png');
binaryImage = im2bw(binaryImage);

% 使用Hough Transform检测直线
[H, theta, rho] = hough(binaryImage);
peaks = houghpeaks(H, 100);
lines = houghlines(binaryImage, theta, rho, peaks);

% 计算每条线段的斜率和截距
slope = zeros(length(lines), 1);
intercept = zeros(length(lines), 1);
for k = 1:length(lines)
    xy = [lines(k).point1; lines(k).point2];
    slope(k) = (xy(2,2) - xy(1,2)) / (xy(2,1) - xy(1,1));
    intercept(k) = xy(1,2) - slope(k) * xy(1,1);
end

% 删除属于同一直线的线段
uniqueLines = true(length(lines), 1);
for k1 = 1:length(lines)
    for k2 = k1+1:length(lines)
        % 判断斜率和截距是否相似
        slopeDiff = abs(slope(k1) - slope(k2));
        interceptDiff = abs(intercept(k1) - intercept(k2));

        % 若相似，则这两条线段属于同一直线
        if slopeDiff < 0.1 && interceptDiff < 10
            uniqueLines(k2) = false;
        end
    end
end

% 绘制删除同一直线后剩余的线段
figure;
imshow(binaryImage);
hold on;
for k = 1:length(lines)
    if uniqueLines(k)
        xy = [lines(k).point1; lines(k).point2];
        plot(xy(:,1), xy(:,2), 'LineWidth', 2, 'Color', 'green');
    end
end
