    hsi_H = iHsi(:,:,1);  % 色调

    % 获取唯一的色调值
    color_type = unique(hsi_H);

    % 创建一个数组来存储具有相同色调的像素
    pixels_matrix = zeros(length(color_type), m*n, 3);

    % 遍历色调值，将具有相同色调的像素放入对应的数组
    for i = 1:length(color_type)
        hue = color_type(i);
        hue_idx = find(iHsi(:,:,1) == hue);
        pixel_data = [iHsi(hue_idx, 1), iHsi(hue_idx, 2), iHsi(hue_idx, 3)];

        % 去除0元素
        non_zero_idx = any(pixel_data, 2);
        pixel_data = pixel_data(non_zero_idx, :);

        pixels_matrix(i, 1:size(pixel_data,1), :) = pixel_data;
    end
end

% 读入图像
image = imread('image.jpg');

% 转换为HSV模式
hsv_image = rgb2hsv(image);

% 获取色调hue通道
hue_channel = hsv_image(:,:,1);

% 初始化标签图
label_image = zeros(size(hue_channel));

% 设置阈值
threshold = 0.05;

% 根据色调hue值给每个像素设置标签
label = 1;
for i = 1:size(hue_channel, 1)
    for j = 1:size(hue_channel, 2)
        if label_image(i, j) == 0
            current_hue = hue_channel(i, j);
            label_image(abs(hue_channel - current_hue) < threshold) = label;
            label = label + 1;
        end
    end
end

% 输出标签图
imshow(label_image, []);

initialGuesses = [-7, -5, 0, 5]; % Здесь укажите начальные приближения, найденные графически
solutions = zeros(size(initialGuesses));

for i = 1:length(initialGuesses)
    solutions(i) = fzero(@equationSolver, initialGuesses(i));
end

% Выводим таблицу с начальными приближениями и соответствующими решениями
T = table(initialGuesses(:), solutions(:), 'VariableNames', {'InitialGuess', 'Solution'})

% 生成一个示例矩阵，存储了坐标信息
% 假设矩阵为10x10的大小
matrix = rand(10, 10, 2); % 生成随机坐标信息
output_matrix = zeros(10, 10); % 创建输出矩阵

% 遍历矩阵元素
for i = 1:size(matrix, 1)
    for j = 1:size(matrix, 2)
        % 获取当前像素点周围八个像素点的坐标
        neighbors = matrix(max(1, i-1):min(size(matrix, 1), i+1), max(1, j-1):min(size(matrix, 2), j+1), :);

        % 计算八个斜率并加权平均
        slopes = zeros(8, 1);
        for n = 1:size(neighbors, 1)
            for m = 1:size(neighbors, 2)
                if n == (i - 1) && m == (j - 1)
                    continue; % 排除中心点
                end

                dx = neighbors(n, m, 1) - matrix(i, j, 1);
                dy = neighbors(n, m, 2) - matrix(i, j, 2);
                slopes(n + 4*(m - 1)) = dy / dx; % 计算斜率
            end
        end

        % 加权平均得到中心像素对应位置的输出值
        weights = ones(size(slopes)) / length(slopes);
        output_matrix(i, j) = sum(slopes.*weights);
    end
end

% 显示输出矩阵
disp(output_matrix);

% 生成一个示例矩阵，存储了坐标信息
% 假设矩阵为10x10的大小
matrix = rand(10, 10, 2); % 生成随机坐标信息
output_matrix = zeros(10, 10); % 创建输出矩阵

% 遍历矩阵元素
for i = 1:size(matrix, 1)
    for j = 1:size(matrix, 2)
        % 获取当前像素点周围八个像素点的坐标
        % 考虑边缘情况的处理
        i_min = max(1, i-1);
        i_max = min(size(matrix, 1), i+1);
        j_min = max(1, j-1);
        j_max = min(size(matrix, 2), j+1);

        neighbors = matrix(i_min:i_max, j_min:j_max, :);

        % 计算斜率并加权平均
        slopes = zeros(8, 1);
        count = 0;
        for n = 1:size(neighbors, 1)
            for m = 1:size(neighbors, 2)
                if n == i && m == j
                    continue; % 排除中心点
                end

                dx = neighbors(n, m, 1) - matrix(i, j, 1);
                dy = neighbors(n, m, 2) - matrix(i, j, 2);

                % 考虑边缘情况，不满足八邻域的像素不计入斜率计算
                if n >= i_min && n <= i_max && m >= j_min && m <= j_max
                    slopes(n + 4*(m - 1)) = dy / dx;
                    count = count + 1;
                end
            end
        end

        % 加权平均得到中心像素对应位置的输出值
        weights = ones(size(slopes)) / count; % 修正权重计算
        output_matrix(i, j) = sum(slopes.*weights);
    end
end

% 显示输出矩阵
disp(output_matrix);

% 假设有几个点的坐标存储在两个向量中：x, y

% 将点的坐标转换为二值图像
img = zeros(300, 300); % 创建一个300x300的全0矩阵
img(sub2ind(size(img), y, x)) = 1; % 将离散点处的像素设为1

% 进行Hough变换
[H,theta,rho] = hough(img);

% 设置Hough变换的峰值阈值
thresh = 0.5*max(H(:));
peaks = houghpeaks(H,5,'Threshold',thresh);

% 提取直线参数
lines = houghlines(img,theta,rho,peaks);

% 输出直线的斜率和截距
for k = 1:length(lines)
    slope = -tand(lines(k).theta);
    intercept = lines(k).rho/sind(lines(k).theta);
    disp(['直线', num2str(k), '： 斜率 = ', num2str(slope), ', 截距 = ', num2str(intercept)]);
end

% 假设有几个点的坐标存储在两个向量中：x, y

% 设置霍夫变换参数
theta = -90:0.5:89; % 角度范围
numTheta = length(theta);
rhoMax = ceil(sqrt(max(x)^2 + max(y)^2)); % 最大极径
numRho = 2*rhoMax;

% 初始化霍夫累加器
H = zeros(numRho, numTheta);

% 遍历每个点，进行霍夫变换
for i = 1:length(x)
    for tIdx = 1:numTheta
        rho = x(i)*cosd(theta(tIdx)) + y(i)*sind(theta(tIdx));
        rhoIdx = round(rho) + rhoMax; % 转换为索引值
        H(rhoIdx, tIdx) = H(rhoIdx, tIdx) + 1;
    end
end

% 设置霍夫变换的峰值阈值
threshold = 0.5*max(H(:));

% 检测霍夫变换的峰值
peaks = houghpeaks(H, 5, 'Threshold', threshold);

% 提取直线参数并输出斜率和截距
for peakIdx = 1:size(peaks, 1)
    rhoIdx = peaks(peakIdx, 1);
    thetaIdx = peaks(peakIdx, 2);

    rho = rhoIdx - rhoMax;
    slope = -tand(theta(thetaIdx));
    intercept = rho / sind(theta(thetaIdx));

    disp(['直线', num2str(peakIdx), ': 斜率 = ', num2str(slope), ', 截距 = ', num2str(intercept)]);
end

