digraph payment_flow {
    rankdir=LR;
    node [shape=box];

    // Define nodes
    shopper [label="Shopper\n(1. Initiates payment)"];
    merchant [label="Merchant\n(2. Submits payment\nrequest to processor)"];
    processor [label="Processor\n(3. Routes request\nto scheme)"];
    scheme [label="Scheme\n(Visa/Mastercard)\n(4. Routes request\nto issuer bank)"];
    issuer_bank [label="Issuer Bank\n(5. Authorizes/\nDeclines payment)"];
    acs [label="ACS\n(6. 3DS Authentication\nif required)"];
    issuer_processor [label="Issuer Processor\n(7. Processes payment\nauthorization)"];
    confirmation [label="Confirmation\n(8. Success/Failure)"];

    // Define edges
    shopper -> merchant;
    merchant -> processor;
    processor -> scheme;
    scheme -> issuer_bank;
    issuer_bank -> acs [label="6A.", style=dotted];
    acs -> issuer_bank [label="6B.", style=dotted];
    issuer_bank -> issuer_processor;
    issuer_processor -> scheme [label="7A. Authorization response"];
    scheme -> processor [label="7B. Response routing"];
    processor -> merchant [label="7C. Final response"];
    merchant -> shopper [label="9. Notify payment status"];

    // Descriptions
    labelloc="t";
    label="Payment Flow involving ACS, Scheme, Issuer Processor, and Bank";
    fontsize=20;
}

