pipeline {
    agent any
    stages {
        stage('Start Job') {
            steps {
                script {
                    def jobName = "my-job" // 需要启动的任务名称
                    def url = "http://jenkins.example.com/job/${jobName}/build" // 启动任务的URL

                    // 创建HTTP POST请求
                    def client = new URL(url).openConnection()
                    client.setRequestMethod("POST")
                    client.setDoOutput(true)

                    // 按需设置请求头
                    client.setRequestProperty("Content-Type", "application/json")
                    client.setRequestProperty("Authorization", "Basic <insert base64-encoded credentials>")
                    client.connect()

                    // 如果需要，可以提供JSON配置
                    def jsonConfig = [
                        parameter: [
                            [
                                name: "param1",
                                value: "value1"
                            ],
                            [
                                name: "param2",
                                value: "value2"
                            ]
                        ]
                    ]

                    // 将JSON配置添加到请求体中
                    OutputStreamWriter out = new OutputStreamWriter(client.getOutputStream())
                    out.write(new JsonBuilder(jsonConfig).toPrettyString())
                    out.close()

                    // 执行请求
                    client.getInputStream()

                    echo "Started job: ${jobName}"
                }
            }
        }
    }
}

