package main

import (
	"fmt"
	"os/exec"
)

func main() {
	// 获取从前端传来的命令行字符串
	command := "echo 'Hello, World!'"

	// 生成执行命令的 Command 对象
	cmd := exec.Command("/bin/bash", "-c", command)

	// 执行命令并等待完成
	output, err := cmd.CombinedOutput()
	if err != nil {
		// 如果执行命令发生错误，则输出错误消息
		fmt.Println("Error executing command:", err)
	} else {
		// 否则输出命令的输出结果
		fmt.Println(string(output))
	}
}

