#include <QTRSensors.h>
#include <NewPing.h>

#define sagmotor1 9
#define sagmotor2 10
#define solmotor1 3
#define solmotor2 11
#define LEDs1 4
#define LEDs2 8
#define MaxMesafe 200
#define MZ80_TRIG 6
#define MZ80_ECHO 5

NewPing MesafeSensoru(MZ80_TRIG, MZ80_ECHO, MaxMesafe);

QTRSensors qtrs(((QTRSensors::EmitterPin)8));
unsigned int sensorValues[8];

void setup()
{
  pinMode(solmotor1, OUTPUT);
  pinMode(solmotor2, OUTPUT);
  pinMode(sagmotor1, OUTPUT);
  pinMode(sagmotor2, OUTPUT);
  pinMode(LEDs1, OUTPUT);
  pinMode(LEDs2, OUTPUT);

  Serial.begin(9600);
  digitalWrite(LEDs1, HIGH);
  digitalWrite(LEDs2, HIGH);

  qtrs.calibrate(QTR_NO_EMITTER_PIN);

  digitalWrite(LEDs1, LOW);
  digitalWrite(LEDs2, LOW); 
  delay(1000);
}

void loop()
{
  unsigned int position = qtrs.readLine(sensorValues);
  int error = (int)position - 3500;
  int mspeed = 100;
  int turn_value = 0;

  if (MesafeAlgila()) {
    mspeed = 0;
  }

  turn_value = error / 20;
  motorControl(mspeed + turn_value, mspeed - turn_value);
}

bool MesafeAlgila()
{
  int mesafe = MesafeSensoru.ping_cm();
  if (mesafe < 20 && mesafe > 0) {
    digitalWrite(LEDs1, HIGH);
    digitalWrite(LEDs2, HIGH);
    return true;
  } else {
    digitalWrite(LEDs1, LOW);
    digitalWrite(LEDs2, LOW);
    return false;
  }
}

void motorControl(int leftSpeed, int rightSpeed) 
{
  if (leftSpeed > 0) {
    digitalWrite(solmotor1, HIGH);
    digitalWrite(solmotor2, LOW);
    analogWrite(solmotor1, abs(leftSpeed));
  } else {
    digitalWrite(solmotor1, LOW);
    digitalWrite(solmotor2, HIGH);
    analogWrite(solmotor2, abs(leftSpeed));
  }

  if (rightSpeed > 0) {
    digitalWrite(sagmotor1, HIGH);
    digitalWrite(sagmotor2, LOW);
    analogWrite(sagmotor1, abs(rightSpeed));
  } else {
    digitalWrite(sagmotor1, LOW);
    digitalWrite(sagmotor2, HIGH);
    analogWrite(sagmotor2, abs(rightSpeed));
  }
}

