using System;
using System.Net;
using System.Net.Sockets;
using System.Text;
using System.Threading.Tasks;

namespace NonBlockingSocketServer
{
    class Program
    {
        static async Task Main(string[] args)
        {
            int port = 8080;
            IPAddress ipAddress = IPAddress.Any;

            TcpListener tcpListener = new TcpListener(ipAddress, port);
            tcpListener.Start();
            Console.WriteLine($"Listening on: {ipAddress}:{port}");

            while (true)
            {
                TcpClient client = await tcpListener.AcceptTcpClientAsync();
                _ = Task.Run(async () => await ProcessClientAsync(client));
            }
        }

        private static async Task ProcessClientAsync(TcpClient client)
        {
            using (client)
            {
                Console.WriteLine($"Connected to: {client.Client.RemoteEndPoint}");
                NetworkStream stream = client.GetStream();
                byte[] buffer = new byte[4096];

                while (true)
                {
                    int bytesRead = await stream.ReadAsync(buffer, 0, buffer.Length);
                    if (bytesRead == 0)
                    {
                        break;
                    }

                    string message = Encoding.ASCII.GetString(buffer, 0, bytesRead);
                    Console.WriteLine($"Received from client: {message.Trim()}");

                    await stream.WriteAsync(buffer, 0, bytesRead);
                    Console.WriteLine("Echo message sent back to client");
                }

                Console.WriteLine($"Disconnected from: {client.Client.RemoteEndPoint}");
            }
        }
    }
}

