def prevBuild = currentBuild.previousBuild
def prevVersion = ''
if (prevBuild != null) {
    def actions = prevBuild.getAllActions()
    for (def action : actions) {
        def buildParams = action.parameters
        if (buildParams != null && buildParams instanceof hudson.model.ParametersAction) {
            def versionParam = buildParams.get("BUMA_BUILD_VERSION")
            if (versionParam != null && versionParam instanceof StringParameterValue) {
                prevVersion = versionParam.value
                break
            }
        }
    }
}

echo "Previous BUMA_BUILD_VERSION value is: ${prevVersion}"

