        }
    }
}

pipeline {
    agent any
    parameters {
        string(name: 'MY_PARAM', defaultValue: 'default_value', description: '')
    }
    stages {
        stage('Read Previous Build Params') {
            steps {
                script {
                    def prevBuild = currentBuild.previousBuild
                    if (prevBuild) {
                        def prevBuildParams = prevBuild.rawBuild.getAction(jenkins.model.ParametersAction.class).parameters
                        def prevBuildMyParam = prevBuildParams.find { it.name == 'MY_PARAM' }?.value

                        if (prevBuildMyParam) {
                            echo "Previous build MY_PARAM: ${prevBuildMyParam}"
                        } else {
                            echo "Previous build MY_PARAM not found"
                        }
                    } else {
                        echo "No previous build found"
                    }
                }
            }
        }
    }
}

