    steps {
        // Archive artifacts and save fingerprints
        sh '''
            mkdir -p archive/
            echo "Hello, World!" > archive/hello.txt
        '''
        archiveArtifacts allowEmptyArchive: true, artifacts: '**/*', fingerprint: true
    }
    post {
        always {
            // Get the MD5 checksum of the artifact
            def artifact = build.getArtifactManager().root().child('archive/hello.txt')
            def md5sum = checksums([file: artifact, algorithm: 'MD5'])
            echo "MD5 checksum of artifact is: ${md5sum}"
        }
    }
}

