use std::process::{Command, Stdio};
use std::io::{Read, Write};
use std::os::windows::io::AsRawHandle;
use encoding_rs::{Decoder, UTF_16LE};

fn main() {
    let output = Command::new("cmd")
        .arg("/C")
        .arg("echo Hello, 你好!")
        .stdout(Stdio::piped())
        .spawn()
        .expect("Failed to execute command")
        .wait_with_output()
        .expect("Failed to read command output");

    let stdout_handle = output.stdout.expect("Failed to capture stdout");
    let handle = stdout_handle.as_raw_handle();
    let mut buffer = vec![0u8; 4096];

    let mut decoder = Decoder::new(UTF_16LE);

    loop {
        let mut bytes_read: u32 = 0;
        unsafe {
            let ret = winapi::um::fileapi::ReadFile(
                handle,
                buffer.as_mut_ptr() as *mut winapi::ctypes::c_void,
                buffer.len() as u32,
                &mut bytes_read,
                std::ptr::null_mut(),
            );

            if ret == 0 {
                panic!("Failed to read from handle");
            }
        }

        if bytes_read == 0 {
            break;
        }

        let decoded = decoder.decode(&buffer[..bytes_read as usize]);
        if let Some(result) = decoded {
            let text = String::from_utf16_lossy(result.0);
            print!("{}", text);
        }
    }
}

