package main

import (
	"fmt"
	"net"
)

const (
	protocol = "udp"
	port     = ":8080"
	buffer   = 1024
)

func main() {
	address, err := net.ResolveUDPAddr(protocol, port)
	if err != nil {
		fmt.Println("Error resolving address:", err)
		return
	}

	conn, err := net.ListenUDP(protocol, address)
	if err != nil {
		fmt.Println("Error listening:", err)
		return
	}
	defer conn.Close()

	fmt.Println("Server started and listening on", address)

	for {
		message := make([]byte, buffer)

		length, clientAddress, err := conn.ReadFromUDP(message)
		if err != nil {
			fmt.Println("Error reading message:", err)
			continue
		}

		fmt.Printf("Received message from %s: %s\n", clientAddress.String(), string(message[:length]))

		// Echo the message back to the client
		_, err = conn.WriteToUDP(message[:length], clientAddress)
		if err != nil {
			fmt.Println("Error writing message:", err)
			continue
		}
	}
}

package main

import (
	"fmt"
	"net"
	"strconv"
)

const (
	protocol = "udp"
	port     = ":8080"
	buffer   = 1024
)

func main() {
	address, err := net.ResolveUDPAddr(protocol, port)
	if err != nil {
		fmt.Println("Error resolving address:", err)
		return
	}

	conn, err := net.ListenUDP(protocol, address)
	if err != nil {
		fmt.Println("Error listening:", err)
		return
	}
	defer conn.Close()

	fmt.Println("Server started and listening on", address)

	for {
		message := make([]byte, buffer)

		length, clientAddress, err := conn.ReadFromUDP(message)
		if err != nil {
			fmt.Println("Error reading message:", err)
			continue
		}

		receivedData := string(message[:length])
		ip, durationStr, threadsStr, method := extractData(receivedData)

		fmt.Printf("Received message from %s:\n", clientAddress.String())
		fmt.Printf("IP: %s\n", ip)
		fmt.Printf("Duration: %s\n", durationStr)
		fmt.Printf("Threads: %s\n", threadsStr)
		fmt.Printf("Method: %s\n", method)

		// Echo the message back to the client
		conn.WriteToUDP([]byte("Message received"), clientAddress)
	}
}

func extractData(message string) (ip string, durationStr string, threadsStr string, method string) {
	data := message[3:] // Skip the first three characters (command ID)

	ipEnd := 0
	for i := 0; i < len(data); i++ {
		if data[i] == '#' {
			ipEnd = i
			break
		}
	}
	ip = data[:ipEnd]

	durationEnd := ipEnd + 1
	for i := ipEnd + 1; i < len(data); i++ {
		if data[i] == '#' {
			durationEnd = i
			break
		}
	}
	durationStr = data[ipEnd+1 : durationEnd]

	threadsEnd := durationEnd + 1
	for i := durationEnd + 1; i < len(data); i++ {
		if data[i] == '#' {
			threadsEnd = i
			break
		}
	}
	threadsStr = data[durationEnd+1 : threadsEnd]

	method = data[threadsEnd+1:]

	return ip, durationStr, threadsStr, method
}

