{
  description = "A flake with combined devShell outputs";

  # Define the inputs for the flake, such as Nixpkgs.
  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixpkgs-unstable";
  };

  outputs = { self, nixpkgs }: {

    # Define the Nix packages for the current system
    packages = forAllSystems (system: 
      let
        pkgs = import nixpkgs {
          inherit system;
        };

        # Define our first derivation
        derivationA = pkgs.stdenv.mkDerivation {
          name = "exampleA";
          # Build dependencies, install phases and other attributes
          buildInputs = [ pkgs.curl ];
        };

        # Define our second derivation
        derivationB = pkgs.stdenv.mkDerivation {
          name = "exampleB";
          # Build dependencies, install phases and other attributes
          buildInputs = [ pkgs.jq ];
        };
      in
      {
          # Combine outputs from two derivations
          combined = pkgs.stdenv.mkDerivation rec {
            name = "combined-${version}";
            version = "1.0";

            buildInputs = [
              derivationA
              derivationB
            ] ++ (with pkgs; [
              # Any extra packages needed for development can be added here
            ]);

            installPhase = ''
              mkdir -p $out/bin
              cp -r ${derivationA}/bin/* $out/bin/ || true
              cp -r ${derivationB}/bin/* $out/bin/ || true
            '';
          };
      }
    );

    # Define a dev shell for the current system
    devShell = forAllSystems (system: 
      let
        pkgs = import nixpkgs {
          inherit system;
        };
      in
      pkgs.mkShell {
        buildInputs = [
          (self.packages.${system}.combined)
        ];

        # Set environment variables and other shell settings
        shellHook = ''
          echo "Welcome to the devShell!"
        '';
      }
    );
  };
}

{
  description = "A devShell combining two derivations";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
  };

  outputs = { self, nixpkgs }: {
    # Define our NixOS system configuration (optional, you might not need this)
    nixosConfigurations.example = nixpkgs.lib.nixosSystem {
      system = "x86_64-linux";
      modules = [];
    };

    # Define the derivations
    packages.x86_64-linux.packageA = nixpkgs.legacyPackages.x86_64-linux.stdenv.mkDerivation {
      name = "packageA";
      # ... configuration for packageA ...
      buildInputs = [ nixpkgs.legacyPackages.x86_64-linux.git ];
    };

    packages.x86_64-linux.packageB = nixpkgs.legacyPackages.x86_64-linux.stdenv.mkDerivation {
      name = "packageB";
      # ... configuration for packageB ...
      buildInputs = [ nixpkgs.legacyPackages.x86_64-linux.htop ];
    };

    # Define the development shell that combines them
    devShell.x86_64-linux = nixpkgs.legacyPackages.x86_64-linux.mkShell {
      buildInputs = with nixpkgs.legacyPackages.x86_64-linux; [
        # Include packageA and packageB in the development shell
        (self.packages.x86_64-linux.packageA)
        (self.packages.x86_64-linux.packageB)
      ];
      # Optional: include other packages in the shell
