      '';
    };
  };
}

% 生成干扰和真实回波信号
N = 256; % 信号长度
t = 0:N-1; % 时间向量
interference = sin(2*pi*5*t/N); % 干扰信号
echo = sin(2*pi*10*t/N); % 真实回波信号
signal = interference + echo; % 包含干扰和真实回波的信号

% 短时傅里叶变换
win_len = 64; % 窗口长度
overlap = 50; % 叠加重叠的百分比
nfft = 256; % FFT点数
[S, f, t] = spectrogram(signal, hamming(win_len), overlap*win_len/100, nfft, 'onesided', 'reassigned');

% 提取复信号的矩阵
complex_matrix = transpose(S);

% 计算相关系数矩阵
corr_matrix = corrcoef(complex_matrix);

% 设置相邻节点间的权重
weight_matrix = corr_matrix;

% 显示结果
figure;
imagesc(real(weight_matrix)); % 显示权重矩阵的实部
colorbar;
xlabel('节点');
ylabel('节点');
title('节点间的权重');

