package main

import (
    "database/sql"
    _ "github.com/go-sql-driver/mysql"
    "github.com/labstack/echo"
    "net/http"
    // Import other required packages
)

func main() {
    // Create a new Echo instance
    e := echo.New()

    // Database setup
    db, err := sql.Open("mysql", "user:password@/dbname?charset=utf8&parseTime=True&loc=Local")
    if err != nil {
        log.Fatal(err)
    }
    defer db.Close()

    // Create a new UserRepository instance
    userRepository := NewUserRepository(db)

    // Define routes
    e.POST("/register", func(c echo.Context) error {
        var user User
        if err := c.Bind(&user); err != nil {
            return err
        }
        if err := c.Validate(&user); err != nil {
            return err
        }
        if err := userRepository.CreateUser(user); err != nil {
            return err
        }
        return c.String(http.StatusOK, "User registered successfully")
    })

    e.GET("/users/:code", func(c echo.Context) error {
        code := c.Param("code")
        user, err := userRepository.GetUser(code)
        if err != nil {
            return err
        }
        if user == nil {
            return c.String(http.StatusNotFound, "User not found")
        }
        return c.JSON(http.StatusOK, user)
    })

    e.PUT("/users/:code", func(c echo.Context) error {
        code := c.Param("code")
        var user User
        if err := c.Bind(&user); err != nil {
            return err
        }
        if err := c.Validate(&user); err != nil {
            return err
        }
        if err := userRepository.UpdateUser(code, user); err != nil {
            return err
        }
        return c.String(http.StatusOK, "User updated successfully")
    })

    e.DELETE("/users/:code", func(c echo.Context) error {
        code := c.Param("code")
        if err := userRepository.DeleteUser(code); err != nil {
            return err
        }
        return c.String(http.StatusOK, "User deleted successfully")
    })

    // Start the server
    e.Start(":8080")
}

