using System.IO;
using System.Windows;
using System.Windows.Controls;
using System.Collections.Generic;

namespace TweakerApp
{
    public partial class MainWindow : Window
    {
        private readonly Dictionary<CheckBox, string> checkBoxScripts = new Dictionary<CheckBox, string>();

        public MainWindow()
        {
            InitializeComponent();

            // CheckBox'ları ve onlara karşılık gelen scriptleri ekle
            checkBoxScripts.Add(checkbox1_1, "echo Batch Script 1-1 Calisti");
            checkBoxScripts.Add(checkbox1_2, "echo Batch Script 1-2 Calisti");
            // ... Diğer CheckBox'lar ve Batch kodları da eklenir
        }

        private void SaveButton_Click(object sender, RoutedEventArgs e)
        {
            StringBuilder scriptContent = new StringBuilder();
            foreach (TreeViewItem category in treeView.Items)
            {
                AppendCheckedItemsScripts(category, scriptContent);
            }

            if (scriptContent.Length > 0)
            {
                SaveScriptToFile(scriptContent.ToString());
            }
            else
            {
                MessageBox.Show("Hiçbir komut seçilmedi.");
            }
        }

        private void AppendCheckedItemsScripts(ItemsControl itemsControl, StringBuilder scriptContent)
        {
            foreach (object item in itemsControl.Items)
            {
                TreeViewItem treeItem = item as TreeViewItem;
                if (treeItem != null)
                {
                    // İç içe TreeViewItem, recursive çağrı
                    AppendCheckedItemsScripts(treeItem, scriptContent);
                    continue;
                }

                CheckBox checkBox = item as CheckBox;
                if (checkBox != null && checkBox.IsChecked == true)
                {
                    // CheckBox'ın ismini kullanarak script içeriğini bul ve ekle
                    if (checkBoxScripts.TryGetValue(checkBox, out var script))
                    {
                        scriptContent.AppendLine(script);
                    }
                }
            }
        }

        private void SaveScriptToFile(string content)
        {
            string filePath = "tweaker.bat";
            // Dosyayı oluştur ve içeriği yaz
            File.WriteAllText(filePath, content);
            MessageBox.Show(filePath + " kaydedildi.");
        }
    }
}

private void SaveButton_Click(object sender, RoutedEventArgs e)
{
    StringBuilder scriptContent = new StringBuilder();

    // Bat dosyasının en başına "echo off" ekler
    scriptContent.AppendLine("@echo off");

    // Her bir TreeViewItem için işleme
    foreach (TreeViewItem treeViewItem in treeView.Items)
    {
        ProcessTreeViewItem(treeViewItem, scriptContent);
    }

    if (scriptContent.Length > "@echo off".Length + Environment.NewLine.Length)
    {
        SaveScriptToFile(scriptContent.ToString());
    }
    else
    {
        MessageBox.Show("Hiçbir komut seçilmedi.");
    }
}

