    }

    // Diğer fonksiyonlarınız...

    private void ProcessTreeViewItem(ItemsControl itemsControl, StringBuilder scriptContent)
    {
        foreach (object obj in itemsControl.Items)
        {
            TreeViewItem subItem = itemsControl.ItemContainerGenerator.ContainerFromItem(obj) as TreeViewItem;

            if (subItem != null)
