using System;
using System.IO;
using System.IO.Pipes;
using System.Threading.Tasks;
using Autodesk.Revit.UI;
using Autodesk.Revit.DB;

public class RevitAddIn : IExternalCommand
{
    private static NamedPipeServerStream pipeServer;
    private const string pipeName = "RevitToRLPipe";

    public Result Execute(ExternalCommandData commandData, ref string message, ElementSet elements)
    {
        // Start the named pipe server in a new thread or as a task to avoid blocking the Revit UI.
        Task.Run(() => 
        {
            try
            {
                using (pipeServer = new NamedPipeServerStream(pipeName, PipeDirection.InOut, 1, PipeTransmissionMode.Byte, PipeOptions.Asynchronous))
                {
                    // Wait for a client to connect
                    pipeServer.WaitForConnection();

                    try
                    {
                        using (StreamReader reader = new StreamReader(pipeServer))
                        using (StreamWriter writer = new StreamWriter(pipeServer))
                        {
                            // You can use StreamReader/StreamWriter or BinaryReader/BinaryWriter to read/write data from/to the pipe stream.
                            // Example: Echo what the client sent
                            string clientInput;
                            while ((clientInput = reader.ReadLine()) != null)
                            {
                                writer.WriteLine(clientInput);
                                writer.Flush();
                            }
                        }
                    }
                    catch (IOException e)
                    {
                        // Handle error here
                        TaskDialog.Show("Pipe Error", e.Message);
                    }
                }
            }
            catch (Exception ex)
            {
                // Handle error here
                TaskDialog.Show("Pipe Error", ex.Message);
            }
        });

        return Result.Succeeded;
    }
}

