    $start_time = Get-Date
    $process = Start-Process 'MyProg.exe' -PassThru
    $time_limit = [TimeSpan]::FromSeconds(1000)

    do {
        if ((Get-Date) - $start_time -gt $time_limit) {
            $process | Stop-Process -Force
            Write-Warning 'MyProg.exe exceeded the time limit of 1000 seconds and was terminated.'
            exit
        }
        Start-Sleep -Seconds 3
    } while (!$process.HasExited)

    $end_time = Get-Date
    $duration = $end_time - $start_time
    Write-Host "Elapsed time: $($duration.ToString('hh\:mm\:ss'))"
}"

@echo off
SET "exePath=MyProg.exe"

powershell -Command "& {
    $process = Start-Process -FilePath '%exePath%' -PassThru
    $stopwatch = [System.Diagnostics.Stopwatch]::StartNew()

    while ($process.HasExited -eq $false) {
        if ($stopwatch.Elapsed.TotalSeconds -gt 1000) {
            $process | Stop-Process -Force
            Write-Output 'WARNING: Process exceeded the time limit and was terminated.'
            break
        }
        Start-Sleep -Seconds 1
    }

    if ($process.HasExited -and $stopwatch.Elapsed.TotalSeconds -le 1000) {
        Write-Output ('Elapsed time: ' + $stopwatch.Elapsed.TotalSeconds + ' seconds.')
    }
}"

