#!/bin/bash

# Definimos la función para mostrar el menú.
mostrar_menu() {
    echo "Gestión de Usuarios"
    echo "-v Inserta –v para visualizar este menú de opciones"
    echo "-i Inserta –i para insertar un nuevo usuario"
    echo "-e Inserta –e para eliminar un usuario"
    echo "-c Inserta –c para cambiar la contraseña de un usuario"
    echo "-m Inserta –m para mostrar la información del usuario"
    echo "-s Inserta –s para salir del programa"
    leer_opcion
}

# Función para leer la opción del usuario.
leer_opcion() {
    read -p "Ingrese la opción deseada: " opcion
    case $opcion in
        -v) mostrar_menu ;;
        -i) agregar_usuario ;;
        -e) eliminar_usuario ;;
        -c) cambiar_contrasena ;;
        -m) mostrar_informacion ;;
        -s) salir ;;
        *) echo "Opción no válida."; mostrar_menu ;;
    esac
}

# Comprobación de la existencia del usuario.
comprobar_usuario() {
    id $1 &> /dev/null
    if [ $? -eq 0 ]; then
        echo "El usuario ya existe."
        return 1
    else
        return 0
    fi
}

# Función para agregar un usuario.
agregar_usuario() {
    read -p "Ingrese el nombre del usuario a agregar: " nombre_usuario
    comprobar_usuario $nombre_usuario
    if [ $? -eq 0 ]; then
        sudo adduser $nombre_usuario
        grep "^$nombre_usuario" /etc/passwd
    else
        mostrar_menu
    fi
}

# Función para eliminar un usuario.
eliminar_usuario() {
    read -p "Ingrese el nombre del usuario a eliminar: " nombre_usuario
    id $nombre_usuario &> /dev/null
    if [ $? -eq 0 ]; then
        sudo deluser $nombre_usuario
        if grep -q "^$nombre_usuario:" /etc/group; then
            echo "Hay un grupo con el nombre del usuario."
        fi
    else
        echo "El usuario no existe."
        mostrar_menu
    fi
}

# Función para cambiar la contraseña de un usuario.
cambiar_contrasena() {
    read -p "Ingrese el nombre del usuario para cambiar la contraseña: " nombre_usuario
    id $nombre_usuario &> /dev/null
    if [ $? -eq 0 ]; then
        sudo passwd $nombre_usuario
    else
        echo "El usuario no existe."
        mostrar_menu
    fi
}

# Función para mostrar información del usuario.
mostrar_informacion() {
    read -p "Ingrese el nombre del usuario para ver la información: " nombre_usuario
    id $nombre_usuario &> /dev/null
    if [ $? -eq 0 ]; then
        chage -l $nombre_usuario
    else
        echo "El usuario no existe."
        mostrar_menu
    fi
}

# Función para salir del programa.
salir() {
    echo "Saliendo del programa. Adiós!"
    exit 0
}

# Verificar que el script se haya llamado con un argumento.
if [ $# -eq 1 ]; then
    opcion=$1
    case $opcion in
        -v) mostrar_menu ;;
        -i) agregar_usuario ;;
        -e) eliminar_usuario ;;
        -c) cambiar_contrasena ;;
        -m) mostrar_informacion ;;
        -s) salir ;;
        *) echo "Opción no válida."; exit 1 ;;
    esac
else
    echo "Por favor, inicie el script con una opción."
    exit 1
fi

