$address = 'localhost';
$port = 1000;

if (($sock = socket_create(AF_INET, SOCK_STREAM, SOL_TCP)) === false) {
    echo "socket_create() fehlgeschlagen: Grund: " . socket_strerror(socket_last_error()) . "\n";
}
if (socket_bind($sock, $address, $port) === false) {
    echo "socket_bind() fehlgeschlagen: Grund: " . socket_strerror(socket_last_error($sock)) . "\n";
}
if (socket_listen($sock, 5) === false) {
    echo "socket_listen() fehlgeschlagen: Grund: " . socket_strerror(socket_last_error($sock)) . "\n";
}
echo "Server läuft und wartet auf Verbindungen bei $address:$port...\n";

$clients = array($sock);

do {
    $read = $clients;
    $write = $except = NULL;
    if (socket_select($read, $write, $except, 0) < 1) {
        continue;
    }

    if (in_array($sock, $read)) {
        if (($msgsock = socket_accept($sock)) === false) {
            echo "socket_accept() fehlgeschlagen: Grund: " . socket_strerror(socket_last_error($sock)) . "\n";
            break;
        } else {
            echo "Verbindung akzeptiert.\n";
            $clients[] = $msgsock;
            $msg = "\nWillkommen zum PHP Test Server.\n";
            socket_write($msgsock, $msg, strlen($msg));
        }
        $key = array_search($sock, $read);
        unset($read[$key]);
    }

    // Handle client input
    foreach ($read as $read_sock) {
        $buf = socket_read($read_sock, 2048, PHP_NORMAL_READ);
        if ($buf === false) {
            echo "socket_read() fehlgeschlagen: Grund: " . socket_strerror(socket_last_error($read_sock)) . "\n";
            break 2;
        }
        if (!$buf = trim($buf)) {
            continue;
        }
        if ($buf == 'quit') {
            break;
        }
        $talkback = "PHP: Du sagst '$buf'.\n";
        socket_write($read_sock, $talkback, strlen($talkback));
        echo "$buf\n";
    }
} while (true);

socket_close($sock);

