case "$muvelet" in
  "+") ((eredmeny=ertek1+ertek2));;
  "-") ((eredmeny=ertek1-ertek2));;
  "*") ((eredmeny=ertek1*ertek2));;
  "/") [[ $ertek2 -eq 0 ]] && { echo "Nullával való osztás hiba!"; exit 2; } || ((eredmeny=ertek1/ertek2));;
  *) echo "Ismeretlen művelet: $muvelet"; exit 3;;
esac

