#!/usr/bin/env bpftrace

kprobe:sys_rt_sigaction
{
    $pid = pid;
    $comm = comm;
    $signum = arg0;
    $new_sigaction = arg1;
    $old_sigaction = (uint64*)arg2;

    // Убедитесь, что мы ищем изменения сигналов для нужного процесса.
    // Замените "myprogram" на имя вашей программы.
    if ($comm == "myprogram")
    {
        // Прочитаем старый обработчик сигнала из аргумента функции 
        $old_handler = *$old_sigaction;

        // Выполним специализированный скрипт с указателем на новый обработчик сигнала
        $new_handler = *(uint64 *)_cast_($new_sigaction, "struct sigaction")->sa_handler;

        printf(
            "Process %s (pid: %d) signal handler change: %d (signal) | 0x%016llx (old) | 0x%016llx (new)\n",
            $comm, $pid, $signum, $old_handler, $new_handler);
    }
}

