$general_words = array();
$preposition_priority = array('for' => 1, 'to' => 2, 'in' => 1);

for ($i = 0; $i < count($words); $i++) {
    $word = $words[$i];

    if (in_array($word, $prepositions)) {
        $next_word = isset($words[$i + 1]) ? $words[$i + 1] : '';
        $priority = isset($preposition_priority[$word]) ? $preposition_priority[$word] : 0;

        if ($priority == 1 && $next_word) {
            $general_words[] = $next_word;
        } elseif ($priority == 2 && $next_word && !in_array($next_word, $general_words)) {
            $general_words[] = $next_word;
        }
    }
}

