$host = 'localhost';
$user = 'utilisateur_mysql';
$password = 'mot_de_passe_mysql';

// Nom du dossier de sauvegarde
$backup_folder = 'backup';

// Date et heure actuelle
$date = date('Y-m-d_H.i.s');

// Connexion à MySQL
$connect = mysqli_connect($host, $user, $password);

// Récupération des noms de toutes les bases de données
$result = mysqli_query($connect, 'SHOW DATABASES');

// Boucle sur chaque base de données
while ($row = mysqli_fetch_assoc($result)) {
    $db_name = $row['Database'];

    // Sauvegarde de la base de données dans un fichier SQL
    $file_name = "$backup_folder/$db_name-$date.sql";
    $command = "mysqldump --opt --user=$user --password=$password --host=$host $db_name > $file_name";
    system($command);
}

// Fermeture de la connexion MySQL
mysqli_close($connect);

