// Parameters
$fn = 50; // Fragment number for smoothness
height = 60; // Overall height of the rook
base_diameter = 40; // Base diameter of the rook
body_diameter = 25; // Body diameter of the rook
crown_diameter = 28; // Diameter of the crown (top)
crown_height = 6; // Height of the crown
merlons = 6; // Number of merlons on the crown
merlon_width = crown_diameter * sin(180/merlons); // Calculate merlon width based on crown diameter and number of merlons
merlon_depth = 5; // Depth of each merlon
cutout_diameter = 20; // Diameter of the top cutout
cutout_depth = 2; // Depth of the top cutout

// Base of the rook
module base() {
    cylinder(h=height/4, d=base_diameter);
}

// Body of the rook
module body() {
    translate([0, 0, height/4])
        cylinder(h=height*3/4, d=body_diameter);
}

// Crown of the rook with merlons (battlements)
module crown() {
    difference() {
        // Main part of the crown
        translate([0, 0, height - crown_height])
            cylinder(h=crown_height, d=crown_diameter);

        // Cutouts to form merlons
        for(i = [0:merlons-1]) {
            rotate([0, 0, i*360/merlons])
                translate([-merlon_width/2, body_diameter/2, height - crown_height + cutout_depth])
                    cube([merlon_width, merlon_depth, crown_height]);
        }
    }
}

// Hollow top cutout
module top_cutout() {
    translate([0, 0, height - cutout_depth])
        cylinder(h=cutout_depth, d=cutout_diameter, $fn=$fn);
}

// Assemble the rook piece
module rook() {
    base();
    body();
    crown();
    top_cutout();
}

// Render the rook
rook();

