foreach ($questionsData as $entry) {
    $stmt = $pdo->prepare('INSERT INTO questions (id, type, label) VALUES (?, ?, ?)');
    $stmt->execute([$entry['uuid'], $entry['type'], $entry['label']]);

    $questionId = $entry['uuid'];

    foreach ($entry['choices'] as $choiceText) {
        $stmt = $pdo->prepare('INSERT INTO choices (question_id, choice_text) VALUES (?, ?)');
        $stmt->execute([$questionId, $choiceText]);

        if (in_array($choiceText, (array)$entry['correct'])) {
            $choiceId = $pdo->lastInsertId();
            $stmt = $pdo->prepare('INSERT INTO correct_answers (question_id, choice_id) VALUES (?, ?)');
            $stmt->execute([$questionId, $choiceId]);
        }
    }

    // Pour les questions de type 'text', insérez directement dans correct_answers
    if ($entry['type'] === 'text') {
        $stmt = $pdo->prepare('INSERT INTO correct_answers (question_id) VALUES (?)');
        $stmt->execute([$questionId]);
    }
}

